mapper.select:
SELECT * FROM
show_tbl_fes show_tbl
WHERE showNum = #{showNum}

mapper.update:
UPDATE
show_tbl_fes show_tbl
SET showName = #{dto.showName}, showCategory = #{dto.showCategory}, showIndate=sysdate, showPlace = #{dto.showPlace}, showPrice = #{dto.showPrice}, showTime = #{dto.showTime}, showAge = #{dto.showAge}, showBene = #{dto.showBene}, curCapacity = #{dto.curCapacity}, maxCapacity = #{dto.maxCapacity}, showDay = #{dto.showDay}, showImage = #{dto.showImage} WHERE showNum = #{dto.showNum}

UPDATE
show_tbl_fes show_tbl
SET show = 'n' WHERE showNum =#{showNum}
