mapper.select:
	SELECT password FROM mvc_customer_tbl
 	WHERE userid=#{strId}
 	AND 'show' = 'y'

SELECT COUNT(*) FROM mvc_customer_tbl
WHERE userid= #{strId}
AND 'show' = 'y'

mapper.update:
UPDATE mvc_customer_tbl
SET enabled = 1 
WHERE email_key = #{email_key}

mapper.select:
